mapper.select:
SELECT a.*,t.name AS typeName FROM article a,type t WHERE a.status = 1 AND a.type=t.id
 AND type=#{type} 
ORDER BY scope DESC

mapper.update:
UPDATE article SET id=#{id}

    ,`title`=#{title}


    ,`describe`=#{describe}


    , `content`=#{content}


    , `scope`=#{scope}


    ,`type`=#{type}

WHERE id=#{id}
